import React, { Component } from 'react';
import styles from './App.module.scss';

import profilePic from "./assets/jpg/jennifer_portrait.JPG";

class App extends Component {
	constructor(props) {
	  super(props);

	  this.state = {
		user: {
			name: "Jennifer",
			photo: profilePic,
			quote: "wee :D",
			pronouns: "She/xe",
			age: 100,
			location: "Remote, GEORGIA United States"
		}
	  };
	}

	render (){
		const user = this.state.user;

		const ProfilePicSection = () => {
			return (
				<div className={styles.profilePic}>
					<h2 className={styles.h1}>{user.name}</h2>
					<div className="">
						<div className={styles.leftPanel}>
							<img 
							height="138px"
							width="170px"
								src={user.photo}
								alt={user.name + "'s profile photo"}
							/>
						</div>
						<div className={styles.rightPanel}>
							<span class={styles.row}>"{user.quote}"</span>
							<span class={styles.row}>
								{user.pronouns}
								<br />
								{user.age} years old
							</span>
							<span class={styles.row}>Last updated: today</span>
						</div>
					</div>
				</div>
			)
		}

		return (
			<div className="App">
				<header className={styles.banner}></header>
				<nav className={styles.navigation}></nav>
				<main className={styles.main}>
					<section className={styles.leftPanel}>
						<ProfilePicSection />
						<div className={styles.contact}></div>
						<div className={styles.interests}></div>
						<div className={styles.details}></div>
						<div className={styles.school}></div>
						<div className={styles.networking}></div>
						<div className={styles.companies}></div>
					</section>
					<section className={styles.rightPanel}>
						<div className={styles.network}>
							<h1>{user.name} is a web developer in your virtual network</h1>
						</div>
						<div className={styles.blog}></div>
						<div className={styles.blurbs}></div>
						<div className={styles.friends}></div>
						<div className={styles.comments}></div>
					</section>
				</main>
				<footer className={styles.footer}></footer>
			</div>
		)
	}
}

