import React from "react";
import styles from "./friends.module.scss";

export const FriendsBox = ({
  title,
  children,
  customClass
}) => {
  const cards = children.map(({name, title, photo, hobbies}, i) => {
    return (
      <div className={styles.card} key={name + "-" + i}>
        <div className="bold">{name}</div>
        <img className={styles.img} src={photo} alt={name + "'s profile photo"} />
        <div>{title}</div>
        <div>{hobbies}</div>
      </div>
    )
  });

  const friendCount = children.length;

  return (
    <div id="friends" className={customClass ? customClass + " " + styles.friend_box : styles.friend_box}>
      <h2>{title}'s Friend Space</h2>
      <div>{title} has <span className="red">{friendCount}</span> Friends.</div>
      {cards}
    </div>
  )
}
